package org.apache.camel.kafkaconnector.cli.bootstrap;import java.io.File;import java.io.IOException;public class UnpackStep {    private final String packageFile;    private final String destination;    public UnpackStep(String packageFile, String destination) {        this.packageFile = packageFile;        this.destination = destination;    }    public void step() throws IOException {        TgzArchiveUnpack tgzArchiveUnpack = new TgzArchiveUnpack();        tgzArchiveUnpack.unpack(new File(packageFile), new File(destination));    }}